function isSubsequence(str1, str2){
    if(str1.length === 0) return true
    
    let l = 0
    let r = 0
    
    while(r < str2.length){
        if(str1[l] === str2[r]){
            l++
        }
        if(l === str1.length){
            return true
        }
        r++
    }
    return false
}

isSubsequence("book", "brooklyn")
isSubsequence("bcd", "")
isSubsequence("abc", "babac")
isSubsequence("hello", "hellow Dear")


